import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { render } from '@testing-library/react';

const categoryList = ['전체', '정치', '경제', '연예', '스포츠', '사회', '생활/문화', '세계', 'IT/과학', '기타' ];


requestAPI = (category) => {
  axios.get('http://localhost:4000')
  .then(data => {
    console.log('카테고리 별 소정이가 보낸 데이터 : ', data, categoryList);
  })
}

const App = () => {

  const [renderData, setRenderData] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:4000')
      .then(data => {
        console.log('소정이가 보낸 전체 데이터 : ', data);
        setRenderData(...data);
      })
    })

    return (
      <div>
        {categoryList.map(item => {
          return <div className="category" onClick={() => requestAPI(item)}>{item}</div>
        })}
        {/* 처음 렌더링 시 모든 데이터를 뿌려준다 */}
        {renderData && (
          <div>
            <p>처음 렌더링 되었으니 모든 데이터를 보여줄게요.</p>
            {renderData.map(item => {
              return <div>{item}</div>
            })}
          </div>
        )}
        {/* 카테고리 클릭 시 해당 카테고리의 내용만 필터링한다 */}
      </div>
    );
}

export default App;
