import React from 'react';
import axios from 'axios';
import './App.css';

const categoryList = ['전체', '정치', '경제', '연예', '스포츠', '사회', '생활/문화', '세계', 'IT/과학', '기타' ];

class App extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        dataList: [],
        isExist: false
      }
      this.handleClick = this.handleClick.bind(this);
    }

    async componentDidMount() {
      await axios
      .get('http://localhost:4000')
      .then(res => {
        return res.data.data;
      })
      .then(data => {
        console.log('! componentDidMount: ', data[0].category);
        this.setState({dataList: data});
      });
    }

    handleClick = (e) => {
      console.log(e);
      const updateRender = async () => {
        await axios
        .get('http://localhost:4000')
        .then(res => {
          return res.data.data;
        })
        .then(data => {

          const sameCategory = data.filter(item => {
            return e === item.category;
          })

          if(!sameCategory) {
            this.setState({ isExist: true })
          }

          this.setState({ dataList: sameCategory });

          // data.map(item => {
          //   if(e !== item.category) {
          //     this.setState({
          //       dataList: this.state.dataList.splice(item.id-1, 1)
          //     })
          //   }
          // })
        });
      }
      // this.state.dataList.map(item => {
      //   console.log(item.category);
      //   // 내가 클릭한 카테고리 이름과 해당 카테고리를 가진 데이터가 일치할 경우 일치한 데이터만 보여준다.
      //   // if(item.category === categoryList[item.category]) {
      //   //   this.setState({ dataList: item});
      //   // }
      // })
      updateRender();
    }

    render() {
      return (
        <div>
          {/* {console.log('categoryList@@@@@@@@', categoryList)} */}
          <div className="category-area">
            {categoryList.map(item => {
            return <div className="category" onClick={() => this.handleClick(item)}>{item}</div>
            })}
          </div>

          {this.state.dataList.map(item => {
            return (
              <div key={item.id} className="data">
                <h2>title</h2>
                <div>{item.title}</div>
                <h2>description</h2>
                <div>{item.text}</div>
                <h2>category</h2>
                <div>{item.category}</div>
              </div>
            )
          })}
        </div>
      );
    }

}

export default App;
