const App = () => {

  // const [renderData, setRenderData] = useState([]);

    useEffect(() => {
      axios
      .get('http://localhost:4000')
      .then(res => {
        return res.data.data;
      })
      // .then(data => {
      //   console.log('야호야호야호야호~', data);
      // })
      .then(data => {
        return (
          <div>
            <div>{data.title}</div>
            <div>{data.text}</div>
            <div>{data.category}</div>
          </div>
        )
      });
    })

    return (
      <div>
        {categoryList.map(item => {
          return <div className="category" onClick={() => console.log(item)}>{item}</div>
        })}

        {/* {renderData.map(item => {
          return (
            <div>
              <div>{item.title}</div>
              <div>{item.text}</div>
              <div>{item.category}</div>
            </div>
          )
        })} */}
      </div>
    );
}

export default App;
