import React from 'react';
import axios from 'axios';
import './App.css';

const categoryList = ['전체', '정치', '경제', '연예', '스포츠', '사회', '생활/문화', '세계', 'IT/과학', '기타' ];


requestAPI = (category) => {
  axios.get('http://localhost:4000')
  .then(data => {
    console.log('소정이가 보낸 데이터 : ', data);
  })
}

class App extends React.Component {

  constructor(props) {
    super(props);
  }

  componentDidMount() {
    axios.get('http://localhost:4000')
  .then(data => {
    console.log('소정이가 보낸 데이터 : ', data);
  })
  }

  render() {
    return (
      <div>
        {categoryList.map(item => {
          return <div className="category" onClick={() => requestAPI(item)}>{item}</div>
        })}
        {/* 처음 렌더링 시 모든 데이터를 뿌려준다 */}

        {/* 카테고리 클릭 시 해당 카테고리의 내용만 필터링한다 */}
      </div>
    );
  }
}

export default App;
